ta/pg:title/@value != \'\' \"&gt; . &lt;xsl:value-of select=\"//pg:meta/pg:title/@value\"/&gt;&lt;/xsl:when&gt;\n			&lt;xsl:when test=\"//pg:meta/pg:linkdesc/@value != \'\' \"&gt; . &lt;xsl:value-of select=\"//pg:meta/pg:linkdesc/@value\"/&gt;&lt;/xsl:when&gt;\n			&lt;xsl:otherwise&gt;&lt;/xsl:otherwise&gt;\n		&lt;/xsl:choose&gt;&lt;/xsl:variable&gt;\n\n		&lt;xsl:variable name=\"title\"&gt;westmetall&lt;xsl:choose&gt;\n			&lt;xsl:when test=\"$tt_multilang = \'true\' and //pg:meta/pg:title[@lang = $tt_lang]/@value != \'\' \"&gt; . &lt;xsl:value-of select=\"//pg:meta/pg:title[@lang = $tt_lang]/@value\"/&gt;&lt;/xsl:when&gt;\n			&lt;xsl:when test=\"$tt_multilang = \'true\' and //pg:meta/pg:linkdesc[@lang = $tt_lang]/@value != \'\' \"&gt; . &lt;xsl:value-of select=\"//pg:meta/pg:linkdesc[@lang = $tt_lang]/@value\"/&gt;&lt;/xsl:when&gt;\n			&lt;xsl:when test=\"//pg:meta/pg:title/@value != \'\' \"&gt; . &lt;xsl:value-of select=\"//pg:meta/pg:title/@value\"/&gt;&lt;/xsl:when&gt;\n			&lt;xsl:when test=\"//pg:meta/pg:linkdesc/@value != \'\' \"&gt; . &lt;xsl:value-of select=\"//pg:meta/pg:linkdesc/@value\"/&gt;&lt;/xsl:when&gt;\n			&lt;xsl:otherwise&gt;&lt;/xsl:otherwise&gt;\n		&lt;/xsl:choose&gt;&lt;/xsl:variable&gt;\n\n		&lt;title&gt;&lt;xsl:value-of select=\"$title\" /&gt;&lt;/title&gt;\n	&lt;/head&gt;\n&lt;/xsl:template&gt;\n</edit:template></pg:template_data><pg:template_data active=\"true\" type=\"html\" lastchange_UTC=\"2007/06/14 10:11:54\" db:id=\"12825\"><edit:template db:id=\"12826\">&lt;!-- \n	CSS STYLESHEET \n--&gt;\n&lt;xsl:template name=\"pg:css\"&gt;\n	&lt;xsl:choose&gt;\n		&lt;xsl:when test=\"$media_type = \'global\'\"&gt;\n			&lt;xsl:call-template name=\"pg:css_global\" /&gt;\n		&lt;/xsl:when&gt;\n		&lt;xsl:when test=\"$media_type = \'screen\'\"&gt;\n			&lt;xsl:call-template name=\"pg:css_screen\" /&gt;\n		&lt;/xsl:when&gt;\n		&lt;xsl:when test=\"$media_type = \'print\'\"&gt;\n			&lt;xsl:call-template name=\"pg:css_print\" /&gt;\n		&lt;/xsl:when&gt;\n\n	&lt;