s\" /&gt;\n				&lt;/xsl:attribute&gt;\n			&lt;/xsl:otherwise&gt;\n		&lt;/xsl:choose&gt;\n\n		&lt;xsl:attribute name=\"target\"&gt;&lt;xsl:value-of select=\"$target\" /&gt;&lt;/xsl:attribute&gt;\n		&lt;xsl:if test=\"$class\"&gt;\n			&lt;xsl:attribute name=\"class\"&gt;&lt;xsl:value-of select=\"$class\" /&gt;&lt;/xsl:attribute&gt;\n		&lt;/xsl:if&gt;\n		&lt;xsl:if test=\"$onFocus\"&gt;\n			&lt;xsl:attribute name=\"onFocus\"&gt;&lt;xsl:value-of select=\"$onFocus\" /&gt;&lt;/xsl:attribute&gt;\n		&lt;/xsl:if&gt;\n		&lt;xsl:if test=\"$onMouseOver\"&gt;\n			&lt;xsl:attribute name=\"onMouseOver\"&gt;&lt;xsl:value-of select=\"$onMouseOver\" /&gt;&lt;/xsl:attribute&gt;\n		&lt;/xsl:if&gt;\n		&lt;xsl:if test=\"$onMouseOut\"&gt;\n			&lt;xsl:attribute name=\"onMouseOut\"&gt;&lt;xsl:value-of select=\"$onMouseOut\" /&gt;&lt;/xsl:attribute&gt;\n		&lt;/xsl:if&gt;\n\n		&lt;!-- get name from meta-information if link is ref to page_id --&gt;\n		&lt;xsl:variable name=\"linkdesc\"&gt;\n			&lt;xsl:if test=\"$href_id\"&gt;\n				&lt;xsl:value-of select=\"document(concat(\'get:page/\', $href_id))//*/pg:meta/pg:linkdesc[@lang = $tt_lang]/@value\" /&gt;\n				\n			&lt;/xsl:if&gt;\n		&lt;/xsl:variable&gt;\n\n		&lt;xsl:choose&gt;\n			&lt;xsl:when test=\"$href_id and not($content) and not($linkdesc = \'\')\"&gt;\n				&lt;xsl:value-of select=\"$linkdesc\" /&gt;\n			&lt;/xsl:when&gt;\n			&lt;xsl:otherwise&gt;\n				&lt;xsl:value-of select=\"$content\" /&gt;\n			&lt;/xsl:otherwise&gt;\n		&lt;/xsl:choose&gt;\n	&lt;/a&gt;\n&lt;/xsl:template&gt;\n</edit:template></pg:template_data><pg:template_data active=\"true\" type=\"html\" lastchange_UTC=\"2007/10/17 15:56:33\" db:id=\"12855\"><edit:template db:id=\"12856\">&lt;!-- edit:text_FORMATTED --&gt;\n&lt;xsl:template name=\"edit:text_formatted\" match=\"edit:text_formatted\"&gt;\n	&lt;xsl:param name=\"class\" /&gt;\n	&lt;xsl:param name=\"id\" /&gt;\n	&lt;xsl:if test=\"($tt_multilang = \'true\' and @lang = $tt_lang) or $tt_multilang != \'true\'\"&gt;\n		&lt;xsl:apply-templates&gt;&lt;xsl:with-param name=\"class\" select=\"$class\"/&gt;&lt;xsl:with-param name=\"id\" select=\"$id\"/&gt;&lt;/xsl:apply-templates&gt;\n	&lt;/xsl:if&gt;\n&lt;/xsl:template&gt;\n\n&lt;!-- edit:text_HEADLINE --&gt;\n&lt;xsl:template match=\"edit:text_headline\"&gt;\n	&lt;xsl:if test=\"($tt_multilang = \'true\' and @lang = $tt_lang) or $tt_multilang != \'true\'\"&gt;\n		&lt;xsl:apply-templates&gt;&lt;xsl:with-param name=\"class\" select=\"\'headline\'\"/&gt;&lt;/xsl:apply-templates&gt;\n	&lt;/xsl:if&gt;\n&lt;/xsl:template&gt;\n</edit:template></pg:template_data><pg:template_data active=\"true\" type=\"html\" lastchange_UTC=\"2008/01/09 12:43:00\" db:id=\"12858\"><edit:template db:id=\"12859\">&lt;!-- TT_P --&gt;\n&lt;xsl:template match=\"p\"&gt;\n	&lt;xsl:param name=\"class\" /&gt;\n	&lt;xsl:param name=\"linebreaks\" /&gt;\n	&lt;xsl:choose&gt;\n		&lt;xsl:when test=\"$class != \'\'\"&gt;\n			&lt;p class=\"{$class}\"&gt;&lt;xsl:apply-templates/&gt;&amp;nbsp;&lt;/p&gt;\n		&lt;/xsl:when&gt;\n		&lt;xsl:when test=\"$linebreaks != \'\'\"&gt;\n			&lt;xsl:apply-templates/&gt;&lt;xsl:if test=\"position() != last()\"&gt;&lt;br /&gt;&lt;/xsl:if&gt;\n		&lt;/xsl:when&gt;\n		&lt;xsl:otherwise&gt;\n			&lt;p&gt;&lt;xsl:apply-templates/&gt;&amp;nbsp;&lt;/p&gt;\n		&lt;/xsl:otherwise&gt;\n	&lt;/xsl:choose&gt;\n&lt;/xsl:template&gt;\n\n&lt;!-- TT_B --&gt;\n&lt;xsl:template match=\"b\"&gt;&lt;b&gt;&lt;xsl:apply-templates/&gt;&lt;/b&gt;&lt;/xsl:template&gt;\n\n&lt;!-- TT_I --&gt;\n&lt;xsl:template match=\"i\"&gt;&lt;i&gt;&lt;xsl:apply-templates/&gt;&lt;/i&gt;&lt;/xsl:template&gt;\n\n&lt;!-- SMALL --&gt;\n&lt;xsl:template match=\"small\"&gt;&lt;small&gt;&lt;xsl:apply-templates /&gt;&lt;/small&gt;&lt;/xsl:template&gt;\n\n&lt;!-- BR --&gt;\n&lt;xsl:template match=\"br\"&gt;&lt;br /&gt;&lt;/xsl:template&gt;\n\n&lt;!-- TT_A --&gt;\n&lt;xsl:template match=\"a\"&gt;\n	&lt;xsl:choose&gt;\n		&lt;xsl:when test=\"substring(@href,1,8) = \'pageref:\'\"&gt;\n			&lt;xsl:call-template name=\"edit:a\"&gt;\n				&lt;xsl:with-param name=\"href_id\" select=\"substring(@href,9)\" /&gt;\n				&lt;xsl:with-param name=\"target\" select=\"@target\" /&gt;\n			&lt;/xsl:call-template&gt;\n		&lt;/xsl:when&gt;\n		&lt;xsl:otherwise&gt;\n			&lt;xsl:call-template name=\"edit:a\"&gt;\n				&lt;xsl:with-param name=\"href\" select=\"@href\" /&gt;\n				&lt;xsl:with-param name=\"target\" select=\"@target\" /&gt;\n			&lt;/xsl:call-template&gt;\n		&lt;/xsl:otherwise&gt;\n	&lt;/xsl:choose&gt;\n&lt;/xsl:template&gt;\n\n</edit:template></pg:template_data><pg:template_data active=\"true\" type=\"debug\" lastchange_UTC=\"2005/06/26 15:53:28\" db:id=\"12861\"><edit:template db:id=\"12862\">&lt;!-- \n	DEBUG ROOT\n--&gt;\n&lt;xsl:template match=\"/\"&gt;\n	&lt;xsl:value-of select=\"document(\'call:doctype/xhtml/1.0/transitional\')\" disable-output-escaping=\"yes\" /&gt;\n	&lt;html&gt;\n		&lt;head&gt;\n			&lt;title&gt;debugging&lt;/title&gt;\n			&lt;style type=\"text/css\"&gt;\n				&lt;xsl:comment&gt;\n				* {\n					font-family : verdana, geneva, arial, tahoma, helvetica, sans-serif;\n					font-size : 11px;\n					line-height : 15px;\n					text-decoration : none;\n					margin-top : 0px;\n					margin-bottom : 0px;\n				}\n				a {\n					font-decoration : none;\n					color : #000000;\n				}\n				a.hover {\n					font-decoration : underline;\n				}\n				&lt;/xsl:comment&gt;\n			&lt;/style&gt;\n		&lt;/head&gt;\n		&lt;body&gt;\n			&lt;table width=\"700px\"&gt;\n				&lt;tr&gt;\n					&lt;td width=\"200px\" valign=\"top\"&gt;\n						&lt;p&gt;&lt;b&gt;[ NAVIGATION ]&lt;/b&gt;&lt;br /&gt;&lt;br /&gt;&lt;/p&gt;\n						&lt;xsl:for-each select=\"document(\'get:navigation\')/proj:pages_struct/*\"&gt;\n							&lt;xsl:call-template name=\"element_navigation\" /&gt;\n						&lt;/xsl:for-each&gt;\n					&lt;/td&gt;\n					&lt;td width=\"20px\" /&gt;\n					&lt;td width=\"480px\" valign=\"top\"&gt;\n						&lt;p&gt;&lt;b&gt;[ DOKUMENT ]&lt;/b&gt;&lt;br /&gt;&lt;br /&gt;&lt;/p&gt;\n						&lt;xsl:for-each select=\"*\"&gt;\n							&lt;xsl:call-template name=\"element\" /&gt;\n						&lt;/xsl:for-each&gt;\n					&lt;/td&gt;\n				&lt;/tr&gt;\n			&lt;/table&gt;\n		&lt;/body&gt;\n	&lt;/html&gt;\n&lt;/xsl:template&gt;\n\n&lt;!-- ELEMENT --&gt;\n&lt;xsl:template match=\"*\" name=\"element\"&gt;\n	&lt;xsl:param name=\"level\" select=\"1\" /&gt;\n	&lt;xsl:if test=\"name() != \'\'\"&gt;\n		&lt;p&gt;\n			&lt;xsl:attribute name=\"style\"&gt;margin-left:&lt;xsl:value-of select=\"$level * 15 - 15\" /&gt;px&lt;/xsl:attribute&gt;&lt;b&gt;&amp;lt;&lt;xsl:value-of select=\"name()\" /&gt;&lt;/b&gt;&amp;nbsp;&lt;xsl:for-each select=\"./@*\"&gt;&lt;xsl:call-template name=\"attribute\" /&gt;&lt;/xsl:for-each&gt;&lt;xsl:if test=\"count(./*) &gt; 0 or not(string(.) = \'\')\"&gt;&lt;b&gt;&amp;gt;&lt;/b&gt;&lt;/xsl:if&gt;&lt;xsl:if test=\"count(./*) = 0 and string(.) = \'\'\"&gt;&lt;b&gt;/&amp;gt;&lt;/b&gt;&lt;/xsl:if&gt;\n		&lt;/p&gt;\n	&lt;/xsl:if&gt;\n	&lt;xsl:if test=\"count(./*) = 0 and not(string(.) = \'\')\"&gt;\n		&lt;xsl:text disable-output-escaping=\"yes\"&gt;&amp;lt;p style=&amp;quot;margin-left:&lt;/xsl:text&gt;&lt;xsl:value-of select=\"$level * 15\" /&gt;px&lt;xsl:text disable-output-escaping=\"yes\"&gt;&amp;quot;&amp;gt;&lt;/xsl:text&gt;\n	&lt;/xsl:if&gt;\n	&lt;xsl:apply-templates&gt;\n		&lt;xsl:with-param name=\"level\" select=\"$level + 1\"&gt;&lt;/xsl:with-param&gt;\n	&lt;/xsl:apply-templates&gt;\n	&lt;xsl:if test=\"count(./*) = 0 and not(string(.) = \'\')\"&gt;\n		&lt;xsl:text disable-output-escaping=\"yes\"&gt;&amp;lt;/p&amp;gt;&lt;/xsl:text&gt;\n	&lt;/xsl:if&gt;\n	&lt;xsl:if test=\"(count(./*) &gt; 0 or not(string(.) = \'\')) and name() != \'\'\"&gt;\n		&lt;p&gt;&lt;xsl:attribute name=\"style\"&gt;	margin-left:&lt;xsl:value-of select=\"$level * 15 - 15\" /&gt;px&lt;/xsl:attribute&gt;&lt;b&gt;&amp;lt;/&lt;xsl:value-of select=\"name()\" /&gt;&amp;gt;&lt;/b&gt;&lt;/p&gt;\n	&lt;/xsl:if&gt;\n&lt;/xsl:template&gt;\n\n&lt;!-- ATTRIBUTE --&gt;\n&lt;xsl:template name=\"attribute\"&gt;\n	&lt;xsl:value-of select=\"name()\" /&gt;=&amp;quot;&lt;span style=\"color:#ff9900;\"&gt;&lt;xsl:value-of select=\".\" /&gt;&lt;/span&gt;&amp;quot;\n&lt;/xsl:template&gt;\n\n&lt;!-- ELEMENT NAVIGATION --&gt;\n&lt;xsl:template name=\"element_navigation\"&gt;\n	&lt;xsl:param name=\"level\" select=\"1\" /&gt;\n	&lt;p&gt;\n		&lt;xsl:attribute name=\"style\"&gt;margin-left:&lt;xsl:value-of select=\"$level * 15 - 15\" /&gt;px&lt;/xsl:attribute&gt;\n	&lt;a&gt;\n		&lt;xsl:attribute name=\"href\"&gt;\n			&lt;xsl:value-of select=\"document(concat(\'pageref:/\', @db:id, \'/\', $tt_lang))/.\" disable-output-escaping=\"yes\" /&gt;\n		&lt;/xsl:attribute&gt;\n		&lt;xsl:choose&gt;\n			&lt;xsl:when test=\"@status = \'active\'\" &gt;\n				&lt;b&gt;&lt;xsl:if test=\"name() = \'pg:folder\'\"&gt;[&lt;/xsl:if&gt;&lt;xsl:value-of select=\"@name\" /&gt;&lt;xsl:if test=\"name() = \'pg:folder\'\"&gt;]&lt;/xsl:if&gt; &amp;nbsp;(&lt;xsl:value-of select=\"@db:id\" /&gt;)&lt;/b&gt;\n			&lt;/xsl:when&gt;\n			&lt;xsl:when test=\"@status = \'parent-of-active\'\" &gt;\n				&lt;b&gt;&lt;span style=\"color:#555555;\"&gt;&lt;xsl:if test=\"name() = \'pg:folder\'\"&gt;[&lt;/xsl:if&gt;&lt;xsl:value-of select=\"@name\" /&gt;&lt;xsl:if test=\"name() = \'pg:folder\'\"&gt;]&lt;/xsl:if&gt;&lt;/span&gt;&lt;/b&gt; &amp;nbsp;(&lt;xsl:value-of select=\"@db:id\" /&gt;)\n			&lt;/xsl:when&gt;\n			&lt;xsl:otherwise&gt;\n				&lt;xsl:if test=\"name() = \'pg:folder\'\"&gt;[&lt;/xsl:if&gt;&lt;xsl:value-of select=\"@name\" /&gt;&lt;xsl:if test=\"name() = \'pg:folder\'\"&gt;]&lt;/xsl:if&gt; &amp;nbsp;(&lt;xsl:value-of select=\"@db:id\" /&gt;)\n			&lt;/xsl:otherwise&gt;\n			&lt;!--xsl:for-each select=\"./@*\"&gt;&lt;xsl:call-template name=\"attribute\" /&gt;&lt;/xsl:for-each--&gt;\n		&lt;/xsl:choose&gt;\n		&lt;/a&gt;\n	&lt;/p&gt;\n	&lt;xsl:for-each select=\"./*\"&gt;\n		&lt;xsl:call-template name=\"element_navigation\"&gt;\n			&lt;xsl:with-param name=\"level\" select=\"$level + 1\"&gt;&lt;/xsl:with-param&gt;\n		&lt;/xsl:call-template&gt;\n	&lt;/xsl:for-each&gt;\n&lt;/xsl:template&gt;\n</edit:template></pg:template_data><pg:template_data type=\"html\" active=\"true\" lastchange_UTC=\"2008/06/08 08:14:46\" db:id=\"12864\"><edit:template db:id=\"12865\">&lt;!--\n	Header\n--&gt;\n&lt;xsl:template name=\"header\"&gt;\n	&lt;div id=\"header\"&gt;\n		&lt;div id=\"middle\"&gt;&lt;a href=\"#navigation\" class=\"hidden\"&gt;Zur Navigation springen&lt;/a&gt;&lt;/div&gt;\n		&lt;div id=\"logo\"&gt;\n			&lt;div class=\"line top\"&gt;&lt;/div&gt;\n			&lt;a&gt;\n				&lt;xsl:attribute name=\"href\"&gt;\n					&lt;xsl:value-of select=\"document(concat(\'pageref:/\', document(\'get:navigation\')/proj:pages_struct/*[1]/@db:id, \'/\', $tt_lang))/.\" /&gt;\n				&lt;/xsl:attribute&gt;\n				&lt;img alt=\"Logo\" class=\"flash_repl\"&gt;&lt;xsl:attribute name=\"src\"&gt;&lt;xsl:value-of select=\"document(\'libref:/global/logo.png\')/.\"/&gt;?link=&lt;xsl:value-of select=\"document(concat(\'pageref:/\', document(\'get:navigation\')/proj:pages_struct/*[1]/@db:id, \'/\', $tt_lang))/.\" disable-output-escaping=\"yes\"/&gt;&lt;xsl:if test=\"$tt_actual_id = document(\'get:navigation\')/proj:pages_struct/*[1]/@db:id\"&gt;&amp;amp;forum=true&lt;/xsl:if&gt;&lt;/xsl:attribute&gt;&lt;/img&gt;&lt;/a&gt;\n		&lt;/div&gt;\n	&lt;/div&gt;\n&lt;/xsl:template&gt;\n</edit:template></pg:template_data><pg:template_data type=\"html\" active=\"true\" lastchange_UTC=\"2007/10/16 17:00:34\" db:id=\"11512\"><edit:template db:id=\"11513\">&lt;xsl:template name=\"depage-powered\"&gt;\n	&lt;div id=\"footer\"&gt;\n		&lt;p&gt;&lt;small&gt;powered by &lt;a href=\"http://depagecms.net\"&gt;depage::cms&lt;/a&gt;&lt;/small&gt;&lt;/p&gt;\n	&lt;/div&gt;\n&lt;/xsl:template&gt;\n</edit:template></pg:template_data><pg:template_data type=\"\" active=\"false\" db:id=\"11609\"><edit:template db:id=\"11610\">\n            <xsl:template match=\"\" db:id=\"11611\">\n		<xsl:apply-templates db:id=\"11613\"/>\n	</xsl:template>\n          </edit:template></pg:template_data><pg:template_data type=\"html\" active=\"true\" lastchange_UTC=\"2008/01/10 16:49:29\" db:id=\"11616\"><edit:template db:id=\"11617\">&lt;!--\n	Abschnitt\n--&gt;\n&lt;xsl:template match=\"sec:section\" name=\"sec:section\"&gt;\n	&lt;div class=\"section\"&gt;\n		&lt;xsl:choose&gt;\n			&lt;xsl:when test=\"(../sec:section[1]/@db:id = @db:id or ../sec:glossary[1]/@db:id = @db:id) and edit:text_headline[@lang = $tt_lang] != \'\'\"&gt;\n			&lt;!--xsl:when test=\"edit:text_headline[@lang = $tt_lang] != \'\'\"--&gt;\n				&lt;div id=\"mainheadline\"&gt;\n					&lt;h1&gt;\n						&lt;xsl:for-each select=\"edit:text_headline[@lang = $tt_lang]\"&gt;\n							&lt;xsl:apply-templates&gt;\n								&lt;xsl:with-param name=\"linebreaks\" select=\"true()\" /&gt;\n							&lt;/xsl:apply-templates&gt;\n						&lt;/xsl:for-each&gt;\n					&lt;/h1&gt;\n				&lt;/div&gt;\n			&lt;/xsl:when&gt;\n			&lt;xsl:when test=\"edit:text_headline[@lang = $tt_lang] != \'\'\"&gt;\n				&lt;h2&gt;\n					&lt;xsl:for-each select=\"edit:text_headline[@lang = $tt_lang]\"&gt;\n						&lt;xsl:apply-templates&gt;\n							&lt;xsl:with-param name=\"linebreaks\" select=\"true()\" /&gt;\n						&lt;/xsl:apply-templates&gt;\n					&lt;/xsl:for-each&gt;\n				&lt;/h2&gt;\n\n			&lt;/xsl:when&gt;\n		&lt;/xsl:choose&gt;\n		&lt;xsl:apply-templates select=\"*[name() != \'edit:text_headline\']\"&gt;\n			&lt;xsl:with-param name=\"first\" select=\"position() = 1\" /&gt;\n		&lt;/xsl:apply-templates&gt;\n	&lt;/div&gt;\n&lt;/xsl:template&gt;\n</edit:template></pg:template_data><pg:template_data type=\"html\" active=\"true\" lastchange_UTC=\"2007/10/17 16:28:25\" db:id=\"11624\"><edit:template db:id=\"11625\">&lt;!--\n	Textblock\n--&gt;\n&lt;xsl:template match=\"sec:text\"&gt;\n	&lt;xsl:param name=\"first\" /&gt;\n	&lt;xsl:if test=\"edit:text_headline[@lang = $tt_lang] != \'\'\"&gt;\n		&lt;xsl:choose&gt;\n			&lt;xsl:when test=\"$first\"&gt;\n				&lt;h2 class=\"first\"&gt;&lt;xsl:value-of select=\"edit:text_headline[@lang = $tt_lang]\" /&gt;&lt;/h2&gt;\n			&lt;/xsl:when&gt;\n			&lt;xsl:otherwise&gt;\n				&lt;h2&gt;&lt;xsl:value-of select=\"edit:text_headline[@lang = $tt_lang]\" /&gt;&lt;/h2&gt;\n			&lt;/xsl:otherwise&gt;\n		&lt;/xsl:choose&gt;\n	&lt;/xsl:if&gt;\n	&lt;xsl:apply-templates select=\"edit:text_formatted\" /&gt;\n&lt;/xsl:template&gt;\n</edit:template></pg:template_data><pg:template_data type=\"html\" active=\"true\" lastchange_UTC=\"2007/05/30 17:10:45\" db:id=\"11769\"><edit:template db:id=\"11770\">&lt;!--\n	Tabelle\n--&gt;\n&lt;xsl:template match=\"edit:table\"&gt;\n	&lt;table&gt;\n		&lt;xsl:apply-templates /&gt;\n	&lt;/table&gt;\n&lt;/xsl:template&gt;\n\n&lt;xsl:template match=\"tr\"&gt;\n	&lt;tr&gt;&lt;xsl:apply-templates select=\"*\" /&gt;&lt;/tr&gt;\n&lt;/xsl:template&gt;\n\n&lt;xsl:template match=\"td\"&gt;\n	&lt;td&gt;\n		&lt;xsl:if test=\"@class\"&gt;\n			&lt;xsl:attribute name=\"class\"&gt;&lt;xsl:value-of select=\"@class\" /&gt;&lt;/xsl:attribute&gt;\n		&lt;/xsl:if&gt;\n		&lt;xsl:if test=\"@colspan\"&gt;\n			&lt;xsl:attribute name=\"colspan\"&gt;&lt;xsl:value-of select=\"@colspan\" /&gt;&lt;/xsl:attribute&gt;\n		&lt;/xsl:if&gt;\n\n		&lt;xsl:apply-templates /&gt;\n	&lt;/td&gt;\n&lt;/xsl:template&gt;\n\n&lt;xsl:template match=\"th\"&gt;\n	&lt;th&gt;\n		&lt;xsl:if test=\"@class\"&gt;\n			&lt;xsl:attribute name=\"class\"&gt;&lt;xsl:value-of select=\"@class\" /&gt;&lt;/xsl:attribute&gt;\n		&lt;/xsl:if&gt;\n		&lt;xsl:if test=\"@colspan\"&gt;\n			&lt;xsl:attribute name=\"colspan\"&gt;&lt;xsl:value-of select=\"@colspan\" /&gt;&lt;/xsl:attribute&gt;\n		&lt;/xsl:if&gt;\n\n		&lt;xsl:apply-templates /&gt;\n	&lt;/th&gt;\n&lt;/xsl:template&gt;\n</edit:template></pg:template_data><pg:template_data type=\"html\" active=\"true\" lastchange_UTC=\"2007/12/19 21:52:15\" db:id=\"12062\"><edit:template db:id=\"12063\">&lt;!--\n	Liste\n--&gt;\n&lt;xsl:template match=\"sec:unordered_list\"&gt;\n	&lt;ul&gt;\n		&lt;xsl:if test=\"@bullets = \'true\'\"&gt;\n			&lt;xsl:attribute name=\"class\"&gt;bullets&lt;/xsl:attribute&gt;\n		&lt;/xsl:if&gt;\n		&lt;xsl:apply-templates select=\"sec:*\" /&gt;\n	&lt;/ul&gt;\n&lt;/xsl:template&gt;\n\n&lt;xsl:template match=\"sec:list_entry\"&gt;\n	&lt;li&gt;\n		&lt;xsl:apply-templates /&gt;\n	&lt;/li&gt;\n&lt;/xsl:template&gt;\n\n&lt;xsl:template match=\"edit:text_headline\"&gt;\n	&lt;xsl:if test=\"@lang = $tt_lang\"&gt;\n	&lt;h3&gt;&lt;xsl:value-of select=\"*\" /&gt;&lt;/h3&gt;\n	&lt;/xsl:if&gt;\n&lt;/xsl:template&gt;\n</edit:template></pg:template_data><pg:template_data type=\"html\" active=\"true\" lastchange_UTC=\"2008/01/11 13:55:13\" db:id=\"13617\"><edit:template db:id=\"13618\">&lt;!--\n	Kontaktinformationen\n--&gt;\n&lt;xsl:template match=\"sec:vcard\"&gt;\n	&lt;div class=\"vcard\"&gt;\n		&lt;xsl:choose&gt;\n			&lt;xsl:when test=\"string-length(edit:text_singleline[@name = \'Name\']/@value) &amp;gt; 0 and string-length(edit:text_singleline[@name = \'Institution\']/@value) &amp;gt; 0\"&gt;\n				&lt;h3 class=\"org\"&gt;&lt;xsl:call-template name=\"vcard_org\" /&gt;&lt;/h3&gt;\n				&lt;p&gt;&lt;xsl:call-template name=\"vcard_name\" /&gt;&lt;/p&gt;\n			&lt;/xsl:when&gt;\n			&lt;xsl:when test=\"string-length(edit:text_singleline[@name = \'Name\']/@value) &amp;gt; 0\"&gt;\n				&lt;h3&gt;&lt;xsl:call-template name=\"vcard_name\" /&gt;&lt;/h3&gt;\n				&lt;xsl:if test=\"string-length(edit:text_singleline[@lang = $tt_lang and @name = \'Abteilung\']/@value) &amp;gt; 0\"&gt;\n					&lt;p class=\"organization-unit\"&gt;\n						&lt;xsl:value-of select=\"edit:text_singleline[@lang = $tt_lang and @name = \'Abteilung\']/@value\" /&gt;\n					&lt;/p&gt;\n				&lt;/xsl:if&gt;\n			&lt;/xsl:when&gt;\n			&lt;xsl:otherwise&gt;\n				&lt;h3 class=\"org fn\"&gt;&lt;xsl:call-template name=\"vcard_org\" /&gt;&lt;/h3&gt;\n			&lt;/xsl:otherwise&gt;\n		&lt;/xsl:choose&gt;\n		&lt;xsl:if test=\"string-length(edit:text_singleline[@name = \'Ort\']/@value) &amp;gt; 0 \"&gt;\n			&lt;div class=\"adr\"&gt;\n				&lt;xsl:if test=\"string-length(edit:text_singleline[@name = \'StraÃŸe\']/@value) &amp;gt; 0 \"&gt;\n					&lt;p class=\"street-address\"&gt;&lt;xsl:value-of select=\"edit:text_singleline[@name = \'StraÃŸe\']/@value\" /&gt;&lt;/p&gt;\n				&lt;/xsl:if&gt;\n				&lt;xsl:if test=\"string-length(edit:text_singleline[@name = \'Postfach\']/@value) &amp;gt; 0 \"&gt;\n					&lt;p class=\"post-office-box\"&gt;Postfach &lt;xsl:value-of select=\"edit:text_singleline[@name = \'Postfach\']/@value\" /&gt;&lt;/p&gt;\n				&lt;/xsl:if&gt;\n				&lt;p&gt;\n					&lt;span class=\"postal-code\"&gt;&lt;xsl:value-of select=\"edit:text_singleline[@name = \'Postleitzahl\']/@value\" /&gt; &lt;/span&gt;\n					&lt;xsl:text&gt; &lt;/xsl:text&gt;\n					&lt;span class=\"locality\"&gt;&lt;xsl:value-of select=\"edit:text_singleline[@name = \'Ort\']/@value\" /&gt;&lt;/span&gt;&lt;/p&gt;\n			&lt;/div&gt;\n		&lt;/xsl:if&gt;\n		&lt;xsl:apply-templates select=\"sec:*\" /&gt;\n	&lt;/div&gt;\n&lt;/xsl:template&gt;\n\n&lt;xsl:template name=\"vcard_org\"&gt;\n	&lt;span class=\"organization-name\"&gt;\n		&lt;xsl:value-of select=\"edit:text_singleline[@name = \'Institution\']/@value\" /&gt;\n	&lt;/span&gt;\n	&lt;xsl:if test=\"string-length(edit:text_singleline[@lang = $tt_lang and @name = \'Abteilung\']/@value) &amp;gt; 0\"&gt;\n		/ &lt;span class=\"organization-unit\"&gt;\n			&lt;xsl:value-of select=\"edit:text_singleline[@lang = $tt_lang and @name = \'Abteilung\']/@value\" /&gt;\n		&lt;/span&gt;\n	&lt;/xsl:if&gt;\n&lt;/xsl:template&gt;\n\n&lt;xsl:template name=\"vcard_name\"&gt;\n	&lt;xsl:if test=\"string-length(edit:text_singleline[@lang = $tt_lang and @name = \'Titel\']/@value) &amp;gt; 0\"&gt;&lt;span class=\"title\"&gt;&lt;xsl:value-of select=\"edit:text_singleline[@lang = $tt_lang and @name = \'Titel\']/@value\" /&gt;&lt;/span&gt;&amp;nbsp;\n	&lt;/xsl:if&gt;\n&lt;span class=\"fn\"&gt;&lt;xsl:value-of select=\"edit:text_singleline[@name = \'Name\']/@value\" /&gt;&lt;/span&gt;\n&lt;/xsl:template&gt;\n\n&lt;xsl:template match=\"sec:vcard_tel\"&gt;\n	&lt;xsl:if test=\"edit:text_singleline[@name = \'Telefon-Nr.\']/@value != \'\' \"&gt;\n	&lt;p class=\"tel\"&gt;\n		Tel &lt;xsl:if test=\"string-length(edit:text_singleline[@lang = $tt_lang and @name = \'Kurzbeschreibung\']/@value) &amp;gt; 0\"&gt; (&lt;xsl:value-of select=\"edit:text_singleline[@lang = $tt_lang and @name = \'Kurzbeschreibung\']/@value\" /&gt;)&lt;/xsl:if&gt; \n		&lt;span class=\"value\"&gt;&lt;xsl:value-of select=\"edit:text_singleline[@name = \'Telefon-Nr.\']/@value\" /&gt;&lt;/span&gt;\n		&lt;span class=\"invisible\"&gt; (&lt;span class=\"type\"&gt;work&lt;/span&gt;)&lt;/span&gt;\n	&lt;/p&gt;\n	&lt;/xsl:if&gt;\n&lt;/xsl:template&gt;\n\n&lt;xsl:template match=\"sec:vcard_fax\"&gt;\n	&lt;xsl:if test=\"edit:text_singleline[@name = \'Fax-Nr.\']/@value != \'\' \"&gt;\n	&lt;p class=\"tel\"&gt;\n		Fax &lt;xsl:if test=\"string-length(edit:text_singleline[@lang = $tt_lang and @name = \'Kurzbeschreibung\']/@value) &amp;gt; 0\"&gt; (&lt;xsl:value-of select=\"edit:text_singleline[@lang = $tt_lang and @name = \'Kurzbeschreibung\']/@value\" /&gt;)&lt;/xsl:if&gt;\n		&lt;span class=\"value\"&gt;&lt;xsl:value-of select=\"edit:text_singleline[@name = \'Fax-Nr.\']/@value\" /&gt;&lt;/span&gt;\n		&lt;span class=\"invisible\"&gt; (&lt;span class=\"type\"&gt;fax&lt;/span&gt;)&lt;/span&gt;\n	&lt;/p&gt;\n	&lt;/xsl:if&gt;\n&lt;/xsl:template&gt;\n\n&lt;xsl:template match=\"sec:vcard_mail\"&gt;\n	&lt;xsl:param name=\"href\"&gt;&lt;xsl:value-of select=\"document(concat(\'call:replaceEmailChars/\', edit:text_singleline[@name = \'E-Mail Adresse\']/@value))\" /&gt;&lt;/xsl:param&gt;\n\n	&lt;xsl:if test=\"edit:text_singleline[@name = \'E-Mail Adresse\']/@value != \'\' \"&gt;\n	&lt;p&gt;\n		&lt;xsl:if test=\"string-length(edit:text_singleline[@lang = $tt_lang and @name = \'Kurzbeschreibung\']/@value) &amp;gt; 0\"&gt;&lt;xsl:value-of select=\"edit:text_singleline[@lang = $tt_lang and @name = \'Kurzbeschreibung\']/@value\" /&gt;&lt;/xsl:if&gt;\n		&lt;a class=\"email\"&gt;\n			&lt;xsl:attribute name=\"href\"&gt;&lt;xsl:value-of select=\"document(concat(\'call:replaceEmailChars/\', \'mailto:\', edit:text_singleline[@name = \'E-Mail Adresse\']/@value))\" /&gt;&lt;/xsl:attribute&gt;\n			&lt;xsl:value-of select=\"$href\" /&gt;\n\n		&lt;/a&gt;\n	&lt;/p&gt;\n	&lt;/xsl:if&gt;\n&lt;/xsl:template&gt;\n\n</edit:template></pg:template_data><pg:template_data active=\"true\" type=\"html\" lastchange_UTC=\"2007/06/14 16:01:05\" db:id=\"13234\"><edit:template db:id=\"13235\">&lt;!-- \n	CSS STYLESHEET \n--&gt;\n&lt;xsl:template name=\"pg:css_screen\"&gt;\n/* {{{ global */ \n* {\n    margin: 0;\n    padding: 0;\n}\nhtml {\n   /* background: #99321C; */\n}\nbody {\n    height: 100%;\n}\n/* }}} */\n/* {{{ background */\n#backcolor, #backcolor_left {\n    background: #dee7ec;\n}\n#backcolor {\n    position: absolute;\n    z-index: 20;\n    top: 0;\n    width: 100%;\n    height: 18em;\n}\n#backcolor_left, #backcolor_left_start {\n    position: absolute;\n    left: 0;\n    padding-top: 18.15em;\n    width: 15em;\n    padding-bottom: 1000em;\n    margin-bottom: -1000em;\n}\n/* }}} */\n/* {{{ content */\n#content {\n    position: absolute;\n    top: 0;\n    left: 0;\n    padding-left: 15em;\n    width: 80em;\n    width: 65em;\n    min-height: 100%;\n    overflow: hidden;\n}\n#content .col {\n    float: left;\n    position: relative;\n    padding-bottom: 1003em;\n    margin-right: 1em;\n    margin-bottom: -1000em;\n}\n#content .topspacer {\n    float: left;\n    width: 80em;\n    height: 18em;\n}\n#content .col div {\n    padding-top: 0;\n}\n#content .col p, #content .col ul, #content .col h2, #content .col h3, #content .col .imgfloatsmall span, #content table td, #content table th, #content table caption {\n    padding-left: 0.5em;\n    padding-right: 0.5em;\n}\n#content .col h1 {\n    padding-top: 0;\n    padding-left: 0.4em;\n    padding-right: 0.4em;\n}\n#content .col h2, #content .col h3 {\n    margin-top: 2em;\n}\n#content .small {\n    width: 19em;\n}\n#content .average {\n    width: 29em;\n}\n#content .wide {\n    width: 43em;\n}\n#content .calendar, #content .fundstueck, #content .maxwidth {\n    width: 60em;\n}\n#content h1 {\n    margin-top: 0em;\n    text-transform: uppercase;\n    padding-bottom: 2em;\n}\n#content h1 span {\n    display: block;\n    float: left;\n    padding: 0.1em 0.4em 0.05em 0.4em;\n    margin-left: -0.4em !important;\n    margin-left: -0.2em;\n    height: 1.6em;\n    color: #ffffff;\n    background-color: #8a0055;\n}\n#content h1.right {\n    margin-right: -0.4em;\n}\n#content h1.right span {\n    float: right;\n}\n#content h1 span.deco {\n    float: none;\n    clear: both;\n    width: 9em;\n    margin-left: -0.4em;\n}\n#content p.date {\n    font-size: 1.2em;\n    padding-bottom: 1.6em;\n}\n#content h2 {\n    clear: both;\n}\n/*#content p, #content ul, #content table {*/\n#content ul, #content table {\n    clear: both;\n    margin-bottom: 2em;\n}\n#content.table {\n    margin-top: 2em;\n}\n/* }}} */\n/* {{{ content lists */\n#content ul {\n    font-size: 1em;\n}\n#content ul.bullets {\n    list-style: disc;\n    margin-left: 1.4em;\n}\n#content .col ul p, #content .col ul h3 {\n    margin: 0;\n    padding: 0;\n}\n#content .col ul a {\n    font-weight: bold;\n}\n#content .col a:hover {\n    text-decoration: underline;\n}\n/* }}} */\n/* {{{ content images */\n#content .small div.fullwidth, #content .small div.fullwidth img {\n    width: 19em;\n}\n#content div.imgfloatsmall {\n    position: absolute;\n    z-index: 40;\n    left: 45em;\n    width: 17em;\n}\n#content div.imgfloatsmall a {\n    display: block;\n    filter:alpha(opacity=0);\n    width: 17em;\n}\n#content div.imgfloatsmall div, #content div.imgfloatsmall div img {\n    width: 17em;\n}\n#content div.imgfloatsmall p, #content div.imgfloatsmall h3, #content div.imgfloatwide p, #content div.imgfloatwide h3 {\n    font-size: 1em;\n    line-height: 1.5em;\n}\n#content div.imgfloatwide {\n    position: relative;\n}\n#content div.imgfloatwide a {\n    display: block;\n    filter:alpha(opacity=0);\n    width: 43em;\n}\n#content div.imgfloatwide div, #content div.imgfloatwide div img {\n    width: 43em;\n}\n#content div.imgfloatwide div.imglegend {\n    position: absolute;\n    z-index: 40;\n    left: 45em;\n    width: 17em;\n    top: 0;\n}\n#content div.imgfloatmax {\n    position: relative;\n}\n#content div.imgfloatmax a {\n    display: block;\n    filter:alpha(opacity=0);\n    width: 60em;\n}\n#content div.imgfloatmax div, #content div.imgfloatmax div img {\n    width: 60em;\n}\n/* }}} */\n/* {{{ content table */\n#content table {\n    margin-top: 2em;\n    margin-bottom: 2em;\n}\n#content table caption {\n    text-align: left;\n}\n#content table th, #content table caption {\n    font-weight: bold;\n}\n#content table small {\n    display: block;\n    font-weight: normal;\n    line-height: 1.5em;\n    padding-bottom: 1em;\n}\n/* }}} */\n/* {{{ content vcard */\n#content div.vcard  a {\n    color: #000000;\n}\n#content div.vcard  div.adr {\n    padding-bottom: 2em;\n}\n/* }}} */\n/* {{{ fundstueck */\n#fundstueck_flash {\n    position: absolute;\n    top: 0;\n    left: 0;\n}\n#fundstueck_flash object {\n    width: 76em;\n    height: 55em;\n}\n#content .fundstueck .img {\n    padding-top: 10em;\n    padding-left: 16em;\n}\n#content .fundstueck .img div, #content .fundstueck .img div img {\n    width: 24em;\n}\n#content .fundstueck .text {\n    padding-top: 3em;\n    padding-left: 0.5em;\n    font-size: 1.4em;\n}\n#content .fundstueck h1 {\n    position: absolute;\n    top: 18em;\n    left: -10em;\n    font-size: 1.5em;\n}\n#content .fundstueck h1 span.year {\n    display: block;\n    position: absolute;\n    top: -1.7em;\n    left: 0.4em;\n    background: none;\n}\n#content .fundstueck .text h3, #content .fundstueck .text p {\n    display: inline;\n    padding: 0 0.5em 0 0;\n    line-height: 1.4em;\n}\n#content .fundstueck .text h3 {\n    font-weight: normal;\n    font-style: italic;\n    font-size: 1.05em;\n}\n#content .fundstueck .text h3 .number {\n    font-weight: bold;\n    font-style: normal;\n    font-size: 0.7em;\n}\n#content .fundstueck .text p {\n    color: #ffffff;\n    font-size: 1em;\n}\n/* }}} */\n/* {{{ navigation */\n#navigation {\n    position: absolute;\n    z-index: 21;\n    left: 0;\n    top: 0;\n    width: 80em;\n    padding-top: 0.7em;\n}\n/* first level */\n#navigation ul {\n    margin-left: 14.5em;\n    padding-top: 0;\n    padding-bottom: 0.5em;\n}\n#navigation ul li {\n    display: inline;\n    line-height: 1.6em;\n}\n#navigation ul li a {\n    padding: 0.20em 0.5em 0.20em 0.5em;\n    background-color: #dee7ec;\n    font-size: 1.25em;\n}\n#navigation ul li a.active, #navigation ul li a:hover {\n    padding: 0.32em 0.5em 0.25em 0.5em;\n    font-weight: bold;\n    text-transform: uppercase;\n    font-size: 1.15em;\n    letter-spacing: 0.1em;\n}\n#navigation ul li a.active {\n    color: #8a0055;\n}\n#navigation ul li a:hover {\n    background-color: #8a0055;\n    color: #ffffff;\n}\n/* second level */\n#subnav {\n    position: absolute;\n    top: 18.5em;\n    left: 0;\n    width: 12.5em;\n}\n#subnav ul {\n    margin-bottom: 0.6em;\n}\n#subnav ul li {\n    display: list-item;\n    line-height: 1.3em;\n    margin-bottom: 0.4em;\n}\n#subnav ul li a {\n    padding: 0;\n    font-weight: bold;\n    font-size: 1.05em;\n}\n#subnav ul li a.active, #subnav ul li a:hover {\n    padding-left: 0;\n    background: none;\n    font-weight: bold;\n    font-size: 1.05em;\n    text-transform: none;\n    letter-spacing: 0;\n}\n#subnav ul li a:hover {\n    color: #8a0055;\n}\n#subnav ul li a.active {\n}\n/* other levels */\n#subnav ul ul {\n    position: relative;\n    top: 0em;\n    left: 0em;\n}\n#subnav ul ul li {\n    display: inline;\n    margin: 0;\n    padding: 0;\n}\n#subnav ul ul li a {\n    display: block;\n    padding: 0.1em 0em 0 1em;\n    margin: 0 0.3em 0.3em -1em;\n    font-weight: normal;\n    font-size: 1.05em;\n}\n#subnav ul ul li a.active, #subnav ul ul li a:hover {\n    padding: 0.1em 0 0 1em;\n    /*background: url(../grfx/all/nav_arrow.png) left top no-repeat;*/\n    font-size: 1.05em;\n}\n#subnav ul ul li a:hover {\n    font-weight: normal;\n}\n#subnav ul ul li a.active:hover {\n    font-weight: bold;\n}\n/* }}} */\n/* {{{ langchooser */\n#langchooser {\n    position: absolute;\n    z-index: 1;\n    left: 0;\n    top: 0;\n    width: 80em;\n    padding-top: 0.7em;\n}\n/* first level */\n#langchooser ul {\n    margin-left: 1em;\n    padding-top: 0;\n    padding-bottom: 0.5em;\n}\n#langchooser ul li {\n    display: inline;\n    line-height: 1.6em;\n}\n#langchooser ul li a {\n    font-weight: bold;\n    padding-right: 1em;\n    font-size: 1em;\n    text-transform: uppercase;\n    color: #ffffff;\n}\n#langchooser ul li a.active, #langchooser ul li a:hover {\n    color: #8a0055;\n}\n/* }}} */\n/* {{{ logo */\n#logo {\n    position: absolute;\n    z-index: 30;\n    left: 0;\n    top: 7em;\n    height: 11em;\n    width: 100%;\n    overflow: hidden;\n}\n#logo .line {\n    position: absolute;\n    top: 0;\n    left: 0;\n    right: 0;\n    height: 10.85em;\n    margin-left: 15em;\n}\n#logo .top {\n    border-top: 0.1em solid #ffffff;\n}\n#logo .bottom {\n    /*border-bottom: 0.1em solid #8a0055;*/\n}\n#logo a, #logo img {\n    display: block;\n    width: 15em;\n    height: 11em;\n}\n#logo object {\n    display: block;\n    width: 74em;\n    height: 11em;\n}\n/* }}} */\n/* {{{ calendar */\n#content .calendar h1 span {\n    position: absolute;\n    z-index: 40;\n    right: 0em;\n    margin-bottom: 4em;\n}\n#content .calendar abbr {\n    border-bottom-width: 0;\n}\n#content .calendar abbr.legend {\n    font-size: 0.85em;\n    padding-right: 0.3em;\n}\n#content .calendar .legend p {\n    position: absolute;\n}\n#content .calendar .legend p.event {\n    z-index: 41;\n    left: 4em;\n}\n#content .calendar .legend p.education {\n    z-index: 42;\n    left: 16.5em;\n}\n#content .calendar .legend p.misc {\n    z-index: 43;\n    