INSERT INTO `tt_newproject_xmldata_elements` (`id`,`id_parent`,`id_doc`,`pos`,`name`,`value`,`type`) VALUES ('11621','12862','0','0',NULL,'<!-- \n	DEBUG ROOT\n-->\n<xsl:template match=\"/\">\n	<xsl:value-of select=\"document(\'call:doctype/xhtml/1.0/transitional\')\" disable-output-escaping=\"yes\" />\n	<html>\n		<head>\n			<title>debugging</title>\n			<style type=\"text/css\">\n				<xsl:comment>\n				* {\n					font-family : verdana, geneva, arial, tahoma, helvetica, sans-serif;\n					font-size : 11px;\n					line-height : 15px;\n					text-decoration : none;\n					margin-top : 0px;\n					margin-bottom : 0px;\n				}\n				a {\n					font-decoration : none;\n					color : #000000;\n				}\n				a.hover {\n					font-decoration : underline;\n				}\n				</xsl:comment>\n			</style>\n		</head>\n		<body>\n			<table width=\"700px\">\n				<tr>\n					<td width=\"200px\" valign=\"top\">\n						<p><b>[ NAVIGATION ]</b><br /><br /></p>\n						<xsl:for-each select=\"document(\'get:navigation\')/proj:pages_struct/*\">\n							<xsl:call-template name=\"element_navigation\" />\n						</xsl:for-each>\n					</td>\n					<td width=\"20px\" />\n					<td width=\"480px\" valign=\"top\">\n						<p><b>[ DOKUMENT ]</b><br /><br /></p>\n						<xsl:for-each select=\"*\">\n							<xsl:call-template name=\"element\" />\n						</xsl:for-each>\n					</td>\n				</tr>\n			</table>\n		</body>\n	</html>\n</xsl:template>\n\n<!-- ELEMENT -->\n<xsl:template match=\"*\" name=\"element\">\n	<xsl:param name=\"level\" select=\"1\" />\n	<xsl:if test=\"name() != \'\'\">\n		<p>\n			<xsl:attribute name=\"style\">margin-left:<xsl:value-of select=\"$level * 15 - 15\" />px</xsl:attribute><b>&lt;<xsl:value-of select=\"name()\" /></b>&nbsp;<xsl:for-each select=\"./@*\"><xsl:call-template name=\"attribute\" /></xsl:for-each><xsl:if test=\"count(./*) > 0 or not(string(.) = \'\')\"><b>&gt;</b></xsl:if><xsl:if test=\"count(./*) = 0 and string(.) = \'\'\"><b>/&gt;</b></xsl:if>\n		</p>\n	</xsl:if>\n	<xsl:if test=\"count(./*) = 0 and not(string(.) = \'\')\">\n		<xsl:text disable-output-escaping=\"yes\">&lt;p style=&quot;margin-left:</xsl:text><xsl:value-of select=\"$level * 15\" />px<xsl:text disable-output-escaping=\"yes\">&quot;&gt;</xsl:text>\n	</xsl:if>\n	<xsl:apply-templates>\n		<xsl:with-param name=\"level\" select=\"$level + 1\"></xsl:with-param>\n	</xsl:apply-templates>\n	<xsl:if test=\"count(./*) = 0 and not(string(.) = \'\')\">\n		<xsl:text disable-output-escaping=\"yes\">&lt;/p&gt;</xsl:text>\n	</xsl:if>\n	<xsl:if test=\"(count(./*) > 0 or not(string(.) = \'\')) and name() != \'\'\">\n		<p><xsl:attribute name=\"style\">	margin-left:<xsl:value-of select=\"$level * 15 - 15\" />px</xsl:attribute><b>&lt;/<xsl:value-of select=\"name()\" />&gt;</b></p>\n	</xsl:if>\n</xsl:template>\n\n<!-- ATTRIBUTE -->\n<xsl:template name=\"attribute\">\n	<xsl:value-of select=\"name()\" />=&quot;<span style=\"color:#ff9900;\"><xsl:value-of select=\".\" /></span>&quot;\n</xsl:template>\n\n<!-- ELEMENT NAVIGATION -->\n<xsl:template name=\"element_navigation\">\n	<xsl:param name=\"level\" select=\"1\" />\n	<p>\n		<xsl:attribute name=\"style\">margin-left:<xsl:value-of select=\"$level * 15 - 15\" />px</xsl:attribute>\n	<a>\n		<xsl:attribute name=\"href\">\n			<xsl:value-of select=\"document(concat(\'pageref:/\', @db:id, \'/\', $tt_lang))/.\" disable-output-escaping=\"yes\" />\n		</xsl:attribute>\n		<xsl:choose>\n			<xsl:when test=\"@status = \'active\'\" >\n				<b><xsl:if test=\"name() = \'pg:folder\'\">[</xsl:if><xsl:value-of select=\"@name\" /><xsl:if test=\"name() = \'pg:folder\'\">]</xsl:if> &nbsp;(<xsl:value-of select=\"@db:id\" />)</b>\n			</xsl:when>\n			<xsl:when test=\"@status = \'parent-of-active\'\" >\n				<b><span style=\"color:#555555;\"><xsl:if test=\"name() = \'pg:folder\'\">[</xsl:if><xsl:value-of select=\"@name\" /><xsl:if test=\"name() = \'pg:folder\'\">]</xsl:if></span></b> &nbsp;(<xsl:value-of select=\"@db:id\" />)\n			</xsl:when>\n			<xsl:otherwise>\n				<xsl:if test=\"name() = \'pg:folder\'\">[</xsl:if><xsl:value-of select=\"@name\" /><xsl:if test=\"name() = \'pg:folder\'\">]</xsl:if> &nbsp;(<xsl:value-of select=\"@db:id\" />)\n			</xsl:otherwise>\n			<!--xsl:for-each select=\"./@*\"><xsl:call-template name=\"attribute\" /></xsl:for-each-->\n		</xsl:choose>\n		</a>\n	</p>\n	<xsl:for-each select=\"./*\">\n		<xsl:call-template name=\"element_navigation\">\n			<xsl:with-param name=\"level\" select=\"$level + 1\"></xsl:with-param>\n		</xsl:call-template>\n	</xsl:for-each>\n</xsl:template>\n','TEXT_NODE');
