INSERT INTO `tt_newproject_xmldata_elements` (`id`,`id_parent`,`id_doc`,`pos`,`name`,`value`,`type`) VALUES ('13236','13235','0','0',NULL,'<!-- \n	CSS STYLESHEET \n-->\n<xsl:template name=\"pg:css_screen\">\n/* {{{ global */ \n* {\n    margin: 0;\n    padding: 0;\n}\nhtml {\n   /* background: #99321C; */\n}\nbody {\n    height: 100%;\n}\n/* }}} */\n/* {{{ background */\n#backcolor, #backcolor_left {\n    background: #dee7ec;\n}\n#backcolor {\n    position: absolute;\n    z-index: 20;\n    top: 0;\n    width: 100%;\n    height: 18em;\n}\n#backcolor_left, #backcolor_left_start {\n    position: absolute;\n    left: 0;\n    padding-top: 18.15em;\n    width: 15em;\n    padding-bottom: 1000em;\n    margin-bottom: -1000em;\n}\n/* }}} */\n/* {{{ content */\n#content {\n    position: absolute;\n    top: 0;\n    left: 0;\n    padding-left: 15em;\n    width: 80em;\n    width: 65em;\n    min-height: 100%;\n    overflow: hidden;\n}\n#content .col {\n    float: left;\n    position: relative;\n    padding-bottom: 1003em;\n    margin-right: 1em;\n    margin-bottom: -1000em;\n}\n#content .topspacer {\n    float: left;\n    width: 80em;\n    height: 18em;\n}\n#content .col div {\n    padding-top: 0;\n}\n#content .col p, #content .col ul, #content .col h2, #content .col h3, #content .col .imgfloatsmall span, #content table td, #content table th, #content table caption {\n    padding-left: 0.5em;\n    padding-right: 0.5em;\n}\n#content .col h1 {\n    padding-top: 0;\n    padding-left: 0.4em;\n    padding-right: 0.4em;\n}\n#content .col h2, #content .col h3 {\n    margin-top: 2em;\n}\n#content .small {\n    width: 19em;\n}\n#content .average {\n    width: 29em;\n}\n#content .wide {\n    width: 43em;\n}\n#content .calendar, #content .fundstueck, #content .maxwidth {\n    width: 60em;\n}\n#content h1 {\n    margin-top: 0em;\n    text-transform: uppercase;\n    padding-bottom: 2em;\n}\n#content h1 span {\n    display: block;\n    float: left;\n    padding: 0.1em 0.4em 0.05em 0.4em;\n    margin-left: -0.4em !important;\n    margin-left: -0.2em;\n    height: 1.6em;\n    color: #ffffff;\n    background-color: #8a0055;\n}\n#content h1.right {\n    margin-right: -0.4em;\n}\n#content h1.right span {\n    float: right;\n}\n#content h1 span.deco {\n    float: none;\n    clear: both;\n    width: 9em;\n    margin-left: -0.4em;\n}\n#content p.date {\n    font-size: 1.2em;\n    padding-bottom: 1.6em;\n}\n#content h2 {\n    clear: both;\n}\n/*#content p, #content ul, #content table {*/\n#content ul, #content table {\n    clear: both;\n    margin-bottom: 2em;\n}\n#content.table {\n    margin-top: 2em;\n}\n/* }}} */\n/* {{{ content lists */\n#content ul {\n    font-size: 1em;\n}\n#content ul.bullets {\n    list-style: disc;\n    margin-left: 1.4em;\n}\n#content .col ul p, #content .col ul h3 {\n    margin: 0;\n    padding: 0;\n}\n#content .col ul a {\n    font-weight: bold;\n}\n#content .col a:hover {\n    text-decoration: underline;\n}\n/* }}} */\n/* {{{ content images */\n#content .small div.fullwidth, #content .small div.fullwidth img {\n    width: 19em;\n}\n#content div.imgfloatsmall {\n    position: absolute;\n    z-index: 40;\n    left: 45em;\n    width: 17em;\n}\n#content div.imgfloatsmall a {\n    display: block;\n    filter:alpha(opacity=0);\n    width: 17em;\n}\n#content div.imgfloatsmall div, #content div.imgfloatsmall div img {\n    width: 17em;\n}\n#content div.imgfloatsmall p, #content div.imgfloatsmall h3, #content div.imgfloatwide p, #content div.imgfloatwide h3 {\n    font-size: 1em;\n    line-height: 1.5em;\n}\n#content div.imgfloatwide {\n    position: relative;\n}\n#content div.imgfloatwide a {\n    display: block;\n    filter:alpha(opacity=0);\n    width: 43em;\n}\n#content div.imgfloatwide div, #content div.imgfloatwide div img {\n    width: 43em;\n}\n#content div.imgfloatwide div.imglegend {\n    position: absolute;\n    z-index: 40;\n    left: 45em;\n    width: 17em;\n    top: 0;\n}\n#content div.imgfloatmax {\n    position: relative;\n}\n#content div.imgfloatmax a {\n    display: block;\n    filter:alpha(opacity=0);\n    width: 60em;\n}\n#content div.imgfloatmax div, #content div.imgfloatmax div img {\n    width: 60em;\n}\n/* }}} */\n/* {{{ content table */\n#content table {\n    margin-top: 2em;\n    margin-bottom: 2em;\n}\n#content table caption {\n    text-align: left;\n}\n#content table th, #content table caption {\n    font-weight: bold;\n}\n#content table small {\n    display: block;\n    font-weight: normal;\n    line-height: 1.5em;\n    padding-bottom: 1em;\n}\n/* }}} */\n/* {{{ content vcard */\n#content div.vcard  a {\n    color: #000000;\n}\n#content div.vcard  div.adr {\n    padding-bottom: 2em;\n}\n/* }}} */\n/* {{{ fundstueck */\n#fundstueck_flash {\n    position: absolute;\n    top: 0;\n    left: 0;\n}\n#fundstueck_flash object {\n    width: 76em;\n    height: 55em;\n}\n#content .fundstueck .img {\n    padding-top: 10em;\n    padding-left: 16em;\n}\n#content .fundstueck .img div, #content .fundstueck .img div img {\n    width: 24em;\n}\n#content .fundstueck .text {\n    padding-top: 3em;\n    padding-left: 0.5em;\n    font-size: 1.4em;\n}\n#content .fundstueck h1 {\n    position: absolute;\n    top: 18em;\n    left: -10em;\n    font-size: 1.5em;\n}\n#content .fundstueck h1 span.year {\n    display: block;\n    position: absolute;\n    top: -1.7em;\n    left: 0.4em;\n    background: none;\n}\n#content .fundstueck .text h3, #content .fundstueck .text p {\n    display: inline;\n    padding: 0 0.5em 0 0;\n    line-height: 1.4em;\n}\n#content .fundstueck .text h3 {\n    font-weight: normal;\n    font-style: italic;\n    font-size: 1.05em;\n}\n#content .fundstueck .text h3 .number {\n    font-weight: bold;\n    font-style: normal;\n    font-size: 0.7em;\n}\n#content .fundstueck .text p {\n    color: #ffffff;\n    font-size: 1em;\n}\n/* }}} */\n/* {{{ navigation */\n#navigation {\n    position: absolute;\n    z-index: 21;\n    left: 0;\n    top: 0;\n    width: 80em;\n    padding-top: 0.7em;\n}\n/* first level */\n#navigation ul {\n    margin-left: 14.5em;\n    padding-top: 0;\n    padding-bottom: 0.5em;\n}\n#navigation ul li {\n    display: inline;\n    line-height: 1.6em;\n}\n#navigation ul li a {\n    padding: 0.20em 0.5em 0.20em 0.5em;\n    background-color: #dee7ec;\n    font-size: 1.25em;\n}\n#navigation ul li a.active, #navigation ul li a:hover {\n    padding: 0.32em 0.5em 0.25em 0.5em;\n    font-weight: bold;\n    text-transform: uppercase;\n    font-size: 1.15em;\n    letter-spacing: 0.1em;\n}\n#navigation ul li a.active {\n    color: #8a0055;\n}\n#navigation ul li a:hover {\n    background-color: #8a0055;\n    color: #ffffff;\n}\n/* second level */\n#subnav {\n    position: absolute;\n    top: 18.5em;\n    left: 0;\n    width: 12.5em;\n}\n#subnav ul {\n    margin-bottom: 0.6em;\n}\n#subnav ul li {\n    display: list-item;\n    line-height: 1.3em;\n    margin-bottom: 0.4em;\n}\n#subnav ul li a {\n    padding: 0;\n    font-weight: bold;\n    font-size: 1.05em;\n}\n#subnav ul li a.active, #subnav ul li a:hover {\n    padding-left: 0;\n    background: none;\n    font-weight: bold;\n    font-size: 1.05em;\n    text-transform: none;\n    letter-spacing: 0;\n}\n#subnav ul li a:hover {\n    color: #8a0055;\n}\n#subnav ul li a.active {\n}\n/* other levels */\n#subnav ul ul {\n    position: relative;\n    top: 0em;\n    left: 0em;\n}\n#subnav ul ul li {\n    display: inline;\n    margin: 0;\n    padding: 0;\n}\n#subnav ul ul li a {\n    display: block;\n    padding: 0.1em 0em 0 1em;\n    margin: 0 0.3em 0.3em -1em;\n    font-weight: normal;\n    font-size: 1.05em;\n}\n#subnav ul ul li a.active, #subnav ul ul li a:hover {\n    padding: 0.1em 0 0 1em;\n    /*background: url(../grfx/all/nav_arrow.png) left top no-repeat;*/\n    font-size: 1.05em;\n}\n#subnav ul ul li a:hover {\n    font-weight: normal;\n}\n#subnav ul ul li a.active:hover {\n    font-weight: bold;\n}\n/* }}} */\n/* {{{ langchooser */\n#langchooser {\n    position: absolute;\n    z-index: 1;\n    left: 0;\n    top: 0;\n    width: 80em;\n    padding-top: 0.7em;\n}\n/* first level */\n#langchooser ul {\n    margin-left: 1em;\n    padding-top: 0;\n    padding-bottom: 0.5em;\n}\n#langchooser ul li {\n    display: inline;\n    line-height: 1.6em;\n}\n#langchooser ul li a {\n    font-weight: bold;\n    padding-right: 1em;\n    font-size: 1em;\n    text-transform: uppercase;\n    color: #ffffff;\n}\n#langchooser ul li a.active, #langchooser ul li a:hover {\n    color: #8a0055;\n}\n/* }}} */\n/* {{{ logo */\n#logo {\n    position: absolute;\n    z-index: 30;\n    left: 0;\n    top: 7em;\n    height: 11em;\n    width: 100%;\n    overflow: hidden;\n}\n#logo .line {\n    position: absolute;\n    top: 0;\n    left: 0;\n    right: 0;\n    height: 10.85em;\n    margin-left: 15em;\n}\n#logo .top {\n    border-top: 0.1em solid #ffffff;\n}\n#logo .bottom {\n    /*border-bottom: 0.1em solid #8a0055;*/\n}\n#logo a, #logo img {\n    display: block;\n    width: 15em;\n    height: 11em;\n}\n#logo object {\n    display: block;\n    width: 74em;\n    height: 11em;\n}\n/* }}} */\n/* {{{ calendar */\n#content .calendar h1 span {\n    position: absolute;\n    z-index: 40;\n    right: 0em;\n    margin-bottom: 4em;\n}\n#content .calendar abbr {\n    border-bottom-width: 0;\n}\n#content .calendar abbr.legend {\n    font-size: 0.85em;\n    padding-right: 0.3em;\n}\n#content .calendar .legend p {\n    position: absolute;\n}\n#content .calendar .legend p.event {\n    z-index: 41;\n    left: 4em;\n}\n#content .calendar .legend p.education {\n    z-index: 42;\n    left: 16.5em;\n}\n#content .calendar .legend p.misc {\n    z-index: 43;\n    left: 29.5em;\n}\n#content .calendar ul {\n    border-top: 1px solid #8a0055;\n    /*border-bottom: 1px solid #ffffff;*/\n    padding-left: 0em;\n    padding-right: 0em;\n    padding-bottom: 0em;\n}\n#content .calendar li.firstchild {\n    padding-top: 6em;\n}\n#content .calendar li.lastchild {\n    padding-bottom: 6em;\n}\n#content .calendar li {\n    overflow: hidden;\n    position: relative;\n    clear: both;\n    margin: 0;\n    padding-top: 0.2em;\n    padding-bottom: 0.2em;\n    padding-left: 0.5em;\n    font-size: 0.88em;\n}\n#content .calendar li p {\n    display: inline;\n}\n#content .calendar li h2 {\n    line-height: 1.5em;\n}\n#content .calendar li div.misc, #content .calendar li div.education, #content .calendar li div.event {\n    position: relative;\n    background-color: #ffffff;\n    margin-bottom: 0em;\n    padding-top: 0.2em !important;\n    padding-top: 0.4em;\n    padding-left: 0.4em;\n}\n#content .calendar li div.event {\n    margin-left: 4em;\n}\n#content .calendar li div.education {\n    margin-left: 17.6em;\n}\n#content .calendar li div.misc {\n    margin-left: 32em;\n}\n#content .calendar span.category {\n    font-size: 0.85em;\n}\n#content .calendar span.day, .calendar span.dayofweek {\n    position: absolute;\n    display: block;\n    text-align: right;\n    font-weight: bold;\n    width: 1.8em;\n    top: 0em;\n}\n#content .calendar span.day {\n    right: 2.6em;\n}\n#content .calendar span.dayofweek {\n    right: 0.6em;\n}\n#content .calendar .exhibits {\n    position: absolute;\n    top: 0;\n    left: 0;\n    padding: 0;\n    margin: 0;\n}\n#content .calendar div.exhib1, #content .calendar div.exhib2, #content .calendar div.exhib3 {\n    position: absolute;\n    top: 0;\n    padding-bottom: 40em;\n    margin-bottom: -40em;\n    width: 1.3em;\n    background-color: #dee7ec;\n}\n#content .calendar div.exhib1 {\n    left: 0;\n}\n#content .calendar div.exhib2 {\n    left: 1.3em;\n}\n#content .calendar div.exhib3 {\n    left: 2.6em;\n}\n/* }}} */\n\n	<xsl:for-each select=\"document(\'get:colors\')//proj:colorscheme[@name]\">\n/* {{{ color <xsl:value-of select=\"@name\"/> */\n.color<xsl:value-of select=\"@name\"/>, #content .color<xsl:value-of select=\"@name\"/> a {\n    background-color: <xsl:value-of select=\"color[@name = \'bg\']/@value\"/> !important;\n    color: <xsl:value-of select=\"color[@name = \'text\']/@value\"/>;\n}\n#content .color<xsl:value-of select=\"@name\"/> h1, .color<xsl:value-of select=\"@name\"/> .col ul a {\n    color: <xsl:value-of select=\"color[@name = \'text_headline\']/@value\"/>;\n}\n/* }}} */\n\n	</xsl:for-each>\n</xsl:template>\n','TEXT_NODE');
