INSERT INTO `tt_newproject_xmldata_elements` (`id`,`id_parent`,`id_doc`,`pos`,`name`,`value`,`type`) VALUES ('13389','13384','0','0',NULL,'<!-- \n	CSS STYLESHEET \n-->\n<xsl:template name=\"pg:css_global\">\n/* {{{ global */\n.invisible {\n    display: none;\n}\n.hidden {\n    visibility: hidden;\n}\n.right, #content table th.right {\n    text-align: right;\n}\nbody {\n    font-family: Georgia, Serif;\n    font-size: 75%;\n}\n#content h1, #content h2, #navigation .active, #navigation a:hover, #langchooser, #subnav ul, .calendar .day, .calendar .dayofweek, .calender h2, .calendar strong, #content table th, #content table caption, #content table small, #content .imglegend, #content .fundstueck h3 .number, #content h3, #content .col ul a {\n    font-family: \"Helvetica Neue\" , Helvetica, Arial, Sans-Serif;\n}\na.nostyle {\n    text-decoration: none;\n    background: none !important;\n}\na img {\n    border: none;\n}\n/* }}} */\n/* {{{ content */\n#content h1 {\n    font-weight: bold;\n    font-size: 1.2em;\n    line-height: 1.6em;\n    letter-spacing: 0.13em;\n}\n#content h2 {\n    font-weight: bold;\n    font-size: 1.1em;\n    line-height: 2em;\n}\n#content p, #content ul, #content table, #content h3 {\n    font-size: 1.13em;\n    line-height: 2em;\n}\n#content table caption{\n    font-size: 1em;\n}\n#content ul li {\n    padding: 0;\n    margin: 0;\n}\n#content p.nospace {\n    margin-top: 0;\n    margin-bottom: 0;\n}\n#content p.space {\n    margin-top: 1.5em;\n}\n#content .crop {\n    margin: 1em 0em 1em 0em;\n    width: 38em;\n    overflow: hidden;\n}\n#content .float_left {\n    float: left;\n    margin: 1em 1em 1em 0em;\n}\n#content address {\n    font-size: 0.97em;\n    line-height: 1.5em;\n    font-style: normal;\n    margin-bottom: 1em;\n}\n#content table {\n    width: 100%;\n    border-spacing: 0;\n}\n#content table th {\n    text-align: left;\n    font-weight: normal;\n}\n#content table td, #content table th {\n    padding-right: 0.7em;\n    font-size: 0.97em;\n    line-height: 1.5em;\n    vertical-align: top;\n    border-collapse: collapse;\n}\n#content table .number {\n    text-align: right;\n}\n/* }}} */\n/* {{{ navigation */\n#navigation ul {\n    line-height: 1.35em;\n    padding-bottom: 1em;\n}\n#navigation ul a {\n}\n#navigation ul li.active a {\n    color: #C10D32;\n}\n#navigation ul a:hover {\n    font-weight: bold;\n    color: #C10D32;\n}\n#navigation ul ul {\n    margin-left: 1em;\n}\n/* }}} */\n/* {{{ lists nav */\nul {\n    list-style: none;\n}\nul a {\n    color: #000000;\n    text-decoration: none;\n}\nul a:hover {\n    color: #555555;\n}\nul a:hover .hidden {\n    visibility: visible;\n}\n/* }}} */\n\n</xsl:template>\n','TEXT_NODE');
